SELECT odoLine.id as id, odoLine.odo_id as odoId, odoLine.linenum as linenum, odoLine.original_linenum as originalLinenum, odoLine.store_id as storeId, odoLine.ext_linenum as extLinenum, odoLine.sku_id as skuId, odoLine.sku_bar_code as skuBarCode, odoLine.sku_name as skuName, odoLine.ext_sku_name as extSkuName, odoLine.qty as qty, odoLine.plan_qty as planQty, odoLine.current_qty as currentQty, odoLine.actual_qty as actualQty, odoLine.cancel_qty as cancelQty, odoLine.assign_qty as assignQty, odoLine.dieking_qty as diekingQty, odoLine.line_price as linePrice, odoLine.line_amt as lineAmt, odoLine.odo_line_status as odoLineStatus, odoLine.is_check as isCheck, odoLine.full_line_outbound as fullLineOutbound, odoLine.part_outbound_strategy as partOutboundStrategy, odoLine.mfg_date as mfgDate, odoLine.exp_date as expDate, odoLine.min_exp_date as minExpDate, odoLine.max_exp_date as maxExpDate, odoLine.batch_number as batchNumber, odoLine.country_of_origin as countryOfOrigin, odoLine.inv_status as invStatus, odoLine.inv_type as invType, odoLine.inv_attr1 as invAttr1, odoLine.inv_attr2 as invAttr2, odoLine.inv_attr3 as invAttr3, odoLine.inv_attr4 as invAttr4, odoLine.inv_attr5 as invAttr5, odoLine.outbound_carton_type as outboundCartonType, odoLine.color as color, odoLine.style as style, odoLine.size as size, odoLine.mixing_attr as mixingAttr, odoLine.original_odo_code as originalOdoCode, odoLine.assign_fail_reason as assignFailReason, odoLine.is_assign_success as isAssignSuccess, odoLine.ou_id as ouId, odoLine.create_time as createTime, odoLine.created_id as createdId, odoLine.last_modify_time as lastModifyTime, odoLine.modified_id as modifiedId, odoLine.wave_code as waveCode FROM t_wh_odo AS odo LEFT JOIN t_wh_odo_line AS odoLine ON odo.id = odoLine.odo_id LEFT JOIN t_wh_sku_inventory AS skuInv ON skuInv.occupation_line_id = odoLine.id LEFT JOIN t_wh_location AS location ON location.allocate_area_id = skuInv.location_id LEFT JOIN t_wh_sku AS sku ON sku.id = skuInv.sku_id LEFT JOIN t_wh_sku_mgmt AS skuMgmt ON skuMgmt.sku_id = sku.id where odo.id in
#{id}
AND odo.ou_id = #{ouId} ORDER BY skuInv.location_id, location.pick_sort, location.code

