mapper.select:
select

from t_wh_odo_vas


        and odo_id = #{odoId}


        and odo_line_id = #{odoLineId}


        and odo_line_id is null


        and vas_type = #{vasType}


        and ou_id = #{ouId}

select odovas.id as id, odovas.odo_id as odoId, odovas.odo_line_id as odoLineId, odovas.vas_type as vasType, odovas.wh_vas_type as whVasType, odovas.express_vas_code as expressVasCode, odovas.print_templet as printTemplet, odovas.sku_bar_code as skuBarCode, odovas.content as content, odovas.carton_no as cartonNo, odovas.qty as qty, odovas.amt as amt, odovas.mode_of_payment as modeOfPayment, odovas.vas_attr1 as vasAttr1, odovas.vas_attr2 as vasAttr2, odovas.vas_attr3 as vasAttr3, odovas.vas_attr4 as vasAttr4, odovas.vas_attr5 as vasAttr5, odovas.ou_id as ouId, whvas.code as whVasTypeCode, sys.dic_label as whVasTypeText, whvas.type as whVasTypeValue from t_wh_odo_vas odovas inner join t_wh_vas whvas on odovas.wh_vas_type=whvas.id left join sys_dictionary sys on sys.dic_value=whvas.type and sys.group_value='WH_VAS_TYPE'
and odovas.odo_id = #{odoId}
and odovas.odo_line_id = #{odoLineId}
and odovas.odo_line_id is null
and odovas.ou_id = #{ouId}

select odovas.id as id, odovas.odo_id as odoId, odovas.odo_line_id as odoLineId, odovas.vas_type as vasType, odovas.wh_vas_type as whVasType, odovas.express_vas_code as expressVasCode, odovas.print_templet as printTemplet, odovas.sku_bar_code as skuBarCode, odovas.content as content, odovas.carton_no as cartonNo, odovas.qty as qty, odovas.amt as amt, odovas.mode_of_payment as modeOfPayment, odovas.vas_attr1 as vasAttr1, odovas.vas_attr2 as vasAttr2, odovas.vas_attr3 as vasAttr3, odovas.vas_attr4 as vasAttr4, odovas.vas_attr5 as vasAttr5, odovas.ou_id as ouId from t_wh_odo_vas odovas
and odovas.odo_id = #{odoId}
and odovas.odo_line_id = #{odoLineId}
and odovas.odo_line_id is null
and odovas.ou_id = #{ouId}

select 
	express_vas_code
from
	t_wh_odo_vas
where
	odo_id = #{odoId}
	and vas_type = #{type}
	and ou_id = #{ouId}
