mapper.select:
select 
    from t_wh_wave_master
    where id=#{id}  and ou_id =#{ouId}

         select count(*)
         from t_wh_wave_master
         where 	ou_id = #{ouId}

					and name = #{name}



					and code = #{code}

           select 
	       from t_wh_wave_master
	       where wave_template_id=#{waveTemplateId}  and ou_id =#{ouId}

           select 
	       from t_wh_wave_master
	       where wave_condition_rule_id=#{waveConditionRuleId}  and ou_id =#{ouId}
