sql.where:
asn.status <> 0 

	and asn.ou_id = #{ouId}


	and asn.asn_code like #{asnCode}


	and asn.asn_ext_code like #{asnExtCode}


	and asn.customer_id = #{customerId}


	and asn.store_id = #{storeId}


	and asn.supplier_id = #{supplierId}


	and asn.logistics_provider_id = #{logisticsProviderId}


	and asn.is_iqc = #{isIqc}


	and asn.asn_type = #{asnType}


	and asn.status = #{status}


	and asn.ext_code = #{extCode}


	and asn.urgent_status = #{urgentStatus}

mapper.update:
UPDATE t_wh_asn SET asn_code = #{asnCode,javaType=java.lang.String,jdbcType=VARCHAR} , asn_ext_code = #{asnExtCode,javaType=java.lang.String,jdbcType=VARCHAR} , po_id = #{poId,javaType=java.lang.Long,jdbcType=BIGINT} , customer_id = #{customerId,javaType=java.lang.Long,jdbcType=BIGINT} , store_id = #{storeId,javaType=java.lang.Long,jdbcType=BIGINT} , po_date = #{poDate,javaType=java.util.Date,jdbcType=TIMESTAMP} , eta = #{eta,javaType=java.util.Date,jdbcType=TIMESTAMP} , delivery_time = #{deliveryTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , qty_planned = #{qtyPlanned,javaType=java.lang.Integer,jdbcType=INTEGER} , qty_rcvd = #{qtyRcvd,javaType=java.lang.Integer,jdbcType=INTEGER} , ctn_planned = #{ctnPlanned,javaType=java.lang.Integer,jdbcType=INTEGER} , ctn_rcvd = #{ctnRcvd,javaType=java.lang.Integer,jdbcType=INTEGER} , supplier_id = #{supplierId,javaType=java.lang.Long,jdbcType=BIGINT} , logistics_provider_id = #{logisticsProviderId,javaType=java.lang.Long,jdbcType=BIGINT} , asn_type = #{asnType,javaType=java.lang.Integer,jdbcType=INTEGER} , status = #{status,javaType=java.lang.Integer,jdbcType=INTEGER} , is_iqc = #{isIqc,javaType=java.lang.Boolean,jdbcType=BIT} , start_time = #{startTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , stop_time = #{stopTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , inbound_time = #{inboundTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , create_time = #{createTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , created_id = #{createdId,javaType=java.lang.Long,jdbcType=BIGINT} , last_modify_time = now() , modified_id = #{modifiedId,javaType=java.lang.Long,jdbcType=BIGINT} , po_ou_id = #{poOuId,javaType=java.lang.Long,jdbcType=BIGINT} , ext_code = #{extCode,javaType=java.lang.String,jdbcType=VARCHAR} , urgent_status = #{urgentStatus,javaType=java.lang.String,jdbcType=VARCHAR}
id = #{id} and last_modify_time = #{lastModifyTime}
and ou_id = #{ouId}

mapper.select:
    	select 
	    from t_wh_asn 
	   
		    asn_ext_code like #{asnExtCode} 
		    and ou_id = #{ouid}
		   
					and status = #{status}

mapper.update:
UPDATE t_wh_asn SET status = #{status},modified_id = #{userid},last_modify_time = now() 

	id in
	
		#{id}
	
   
			and ou_id = #{ouid}

mapper.select:
