sql.where:
(line.uuid = '' or line.uuid is null or line.uuid = #{uuid}) 
and po.id = line.po_id 

	and line.ou_id = #{ouId}


	and line.po_id = #{poId}

mapper.update:
UPDATE t_wh_po_line SET po_id = #{poId,javaType=java.lang.Long,jdbcType=BIGINT} , linenum = #{linenum,javaType=java.lang.Integer,jdbcType=INTEGER} , sku_id = #{skuId,javaType=java.lang.Long,jdbcType=BIGINT} , qty_planned = #{qtyPlanned,javaType=java.lang.Integer,jdbcType=INTEGER} , overshipped = #{overshipped,javaType=java.lang.Integer,jdbcType=INTEGER} , available_qty = #{availableQty,javaType=java.lang.Integer,jdbcType=INTEGER} , ctn_planned = #{ctnPlanned,javaType=java.lang.Integer,jdbcType=INTEGER} , qty_rcvd = #{qtyRcvd,javaType=java.lang.Integer,jdbcType=INTEGER} , ctn_rcvd = #{ctnRcvd,javaType=java.lang.Integer,jdbcType=INTEGER} , status = #{status,javaType=java.lang.Integer,jdbcType=INTEGER} , is_iqc = #{isIqc,javaType=java.lang.Boolean,jdbcType=BIT} , mfg_date = #{mfgDate,javaType=java.util.Date,jdbcType=TIMESTAMP} , exp_date = #{expDate,javaType=java.util.Date,jdbcType=TIMESTAMP} , valid_date = #{validDate,javaType=java.lang.Integer,jdbcType=INTEGER} , batch_no = #{batchNo,javaType=java.lang.String,jdbcType=VARCHAR} , country_of_origin = #{countryOfOrigin,javaType=java.lang.String,jdbcType=VARCHAR} , inv_status = #{invStatus,javaType=java.lang.Long,jdbcType=BIGINT} , inv_attr1 = #{invAttr1,javaType=java.lang.String,jdbcType=VARCHAR} , inv_attr2 = #{invAttr2,javaType=java.lang.String,jdbcType=VARCHAR} , inv_attr3 = #{invAttr3,javaType=java.lang.String,jdbcType=VARCHAR} , inv_attr4 = #{invAttr4,javaType=java.lang.String,jdbcType=VARCHAR} , inv_attr5 = #{invAttr5,javaType=java.lang.String,jdbcType=VARCHAR} , create_time = #{createTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , created_id = #{createdId,javaType=java.lang.Long,jdbcType=BIGINT} , last_modify_time = now(), modified_id = #{modifiedId,javaType=java.lang.Long,jdbcType=BIGINT} , uuid = #{uuid,javaType=java.lang.String,jdbcType=VARCHAR}
id = #{id} and last_modify_time = #{lastModifyTime}
and ou_id = #{ouId}

mapper.delete:
  
		delete from t_wh_po_line line
		
			line.po_id = #{poid} 
			and line.uuid = #{uuid}
			
				and line.ou_id = #{ouid}

mapper.select:
select
from t_wh_po_line line left join sys_dictionary sys on sys.dic_value = line.status and sys.group_value = 'POSTATUS' left join t_wh_inventory_status inv on inv.id = line.inv_status left join t_wh_po po on po.id=line.po_id where line.id=#{id}
and line.ou_id = #{ouId}

mapper.update:
UPDATE t_wh_po_line SET status = #{status},modified_id = #{userid},last_modify_time = now() 

	id in
	
		#{id}
	
	 and last_modify_time <=  #{lastModifyTime} 
	   
				and ou_id = #{ouid}
