sql.where:
(line.uuid = '' or line.uuid is null or line.uuid = #{uuid}) 

	and line.ou_id = #{ouId}


	and line.po_id = #{poId}

mapper.update:
UPDATE t_wh_po_line SET po_id = #{poId,javaType=java.lang.Long,jdbcType=BIGINT} , linenum = #{linenum,javaType=java.lang.Integer,jdbcType=INTEGER} , sku_id = #{skuId,javaType=java.lang.Long,jdbcType=BIGINT} , qty_planned = #{qtyPlanned,javaType=java.lang.Double,jdbcType=DECIMAL} , overshipped = #{overshipped,javaType=java.lang.Double,jdbcType=DECIMAL} , available_qty = #{availableQty,javaType=java.lang.Double,jdbcType=DECIMAL} , ctn_planned = #{ctnPlanned,javaType=java.lang.Integer,jdbcType=INTEGER} , qty_rcvd = #{qtyRcvd,javaType=java.lang.Double,jdbcType=DECIMAL} , ctn_rcvd = #{ctnRcvd,javaType=java.lang.Integer,jdbcType=INTEGER} , status = #{status,javaType=java.lang.Integer,jdbcType=INTEGER} , is_iqc = #{isIqc,javaType=java.lang.Boolean,jdbcType=BIT} , mfg_date = #{mfgDate,javaType=java.util.Date,jdbcType=TIMESTAMP} , exp_date = #{expDate,javaType=java.util.Date,jdbcType=TIMESTAMP} , valid_date = #{validDate,javaType=java.lang.Integer,jdbcType=INTEGER} , batch_no = #{batchNo,javaType=java.lang.String,jdbcType=VARCHAR} , country_of_origin = #{countryOfOrigin,javaType=java.lang.String,jdbcType=VARCHAR} , inv_status = #{invStatus,javaType=java.lang.Long,jdbcType=BIGINT} , inv_type = #{invType,javaType=java.lang.String,jdbcType=VARCHAR}, inv_attr1 = #{invAttr1,javaType=java.lang.String,jdbcType=VARCHAR} , inv_attr2 = #{invAttr2,javaType=java.lang.String,jdbcType=VARCHAR} , inv_attr3 = #{invAttr3,javaType=java.lang.String,jdbcType=VARCHAR} , inv_attr4 = #{invAttr4,javaType=java.lang.String,jdbcType=VARCHAR} , inv_attr5 = #{invAttr5,javaType=java.lang.String,jdbcType=VARCHAR} , create_time = #{createTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , created_id = #{createdId,javaType=java.lang.Long,jdbcType=BIGINT} , last_modify_time = now(), modified_id = #{modifiedId,javaType=java.lang.Long,jdbcType=BIGINT} , uuid = #{uuid,javaType=java.lang.String,jdbcType=VARCHAR} , po_line_id = #{poLineId,javaType=java.lang.Long,jdbcType=BIGINT} , valid_date_uom = #{validDateUom,javaType=java.lang.String,jdbcType=VARCHAR}
id = #{id} and last_modify_time = #{lastModifyTime}
and ou_id = #{ouId}

mapper.select:
        select count(*) from 
	        t_wh_po_line line 
	        inner join t_wh_po po on po.id=line.po_id
			left join sys_dictionary sys on sys.dic_value = line.status and sys.group_value = 'POLINESTATUS'
			left join t_wh_inventory_status inv on inv.id = line.inv_status
			left join t_wh_sku sku on line.sku_id = sku.id
			left join t_wh_uom uom on uom.uom_code=line.valid_date_uom
		
			po.po_code=#{poCode}
			and po.uuid=#{uuid}
			and line.uuid=#{uuid}

mapper.delete:
  
		delete from t_wh_po_line
		
			po_id = #{poid} 
			and uuid = #{uuid}
			
				and ou_id = #{ouid}

  
		delete from t_wh_po_line
		
			id = #{id} 
			
				and ou_id = #{ouid}

  
		delete from t_wh_po_line
		
			po_id = #{poid} 
			and uuid <> #{uuid}
			
				and ou_id = #{ouid}

  
		delete from t_wh_po_line
		
			po_id = #{poid} 
			and uuid is not null 
			
				and ou_id = #{ouid}

mapper.select:
select
from t_wh_po_line line left join sys_dictionary sys on sys.dic_value = line.status and sys.group_value = 'POLINESTATUS' left join t_wh_inventory_status inv on inv.id = line.inv_status left join t_wh_po po on po.id=line.po_id left join t_wh_sku sku on line.sku_id = sku.id left join t_wh_uom uom on uom.uom_code=line.valid_date_uom where line.id=#{id}
and line.ou_id = #{ouId}

