sql.where:
po.status <> 0 

	and po.ou_id = #{ouId}


	and po.po_code like #{poCode}


	and po.ext_code like #{extCode}


	and po.customer_id = #{customerId}


	and po.store_id = #{storeId}


	and po.supplier_id = #{supplierId}


	and po.logistics_provider_id = #{logisticsProviderId}


	and po.is_iqc = #{isIqc}

mapper.update:
UPDATE t_wh_po SET po_code = #{poCode,javaType=java.lang.String,jdbcType=VARCHAR} , ext_code = #{extCode,javaType=java.lang.String,jdbcType=VARCHAR} , customer_id = #{customerId,javaType=java.lang.Long,jdbcType=BIGINT} , store_id = #{storeId,javaType=java.lang.Long,jdbcType=BIGINT} , supplier_id = #{supplierId,javaType=java.lang.Long,jdbcType=BIGINT} , logistics_provider_id = #{logisticsProviderId,javaType=java.lang.Long,jdbcType=BIGINT} , po_type = #{poType,javaType=java.lang.Integer,jdbcType=INTEGER} , status = #{status,javaType=java.lang.Integer,jdbcType=INTEGER} , is_iqc = #{isIqc,javaType=java.lang.Boolean,jdbcType=BIT} , po_date = #{poDate,javaType=java.util.Date,jdbcType=TIMESTAMP} , eta = #{eta,javaType=java.util.Date,jdbcType=TIMESTAMP} , delivery_time = #{deliveryTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , qty_planned = #{qtyPlanned,javaType=java.lang.Integer,jdbcType=INTEGER} , qty_rcvd = #{qtyRcvd,javaType=java.lang.Integer,jdbcType=INTEGER} , ctn_planned = #{ctnPlanned,javaType=java.lang.Integer,jdbcType=INTEGER} , ctn_rcvd = #{ctnRcvd,javaType=java.lang.Integer,jdbcType=INTEGER} , start_time = #{startTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , stop_time = #{stopTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , inbound_time = #{inboundTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , is_wms = #{isWms,javaType=java.lang.Boolean,jdbcType=BIT} , is_vmi = #{isVmi,javaType=java.lang.Boolean,jdbcType=BIT} , create_time = #{createTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , created_id = #{createdId,javaType=java.lang.Long,jdbcType=BIGINT} , last_modify_time = #{lastModifyTime,javaType=java.util.Date,jdbcType=TIMESTAMP} , modified_id = #{modifiedId,javaType=java.lang.Long,jdbcType=BIGINT}
id = #{id} and last_modify_time <= #{lastModifyTime}
and ou_id = #{ouId}

mapper.select:
    	select 
		    from 
		    t_wh_po 
		    
		    	id = #{id}
				
		   			 and ou_id = #{ouid}

mapper.update:
UPDATE t_wh_po SET status = #{status},modified_id = #{userid},last_modify_time = now() 

	id in
	
		#{id}
	
	 and last_modify_time <=  #{lastModifyTime} 
	   
				and ou_id = #{ouid}

mapper.select:
        select count(*) from 
        t_wh_po
		
			
				and ou_id = #{ouId}
			
			
				and po_code = #{poCode}
			
			
				and store_id = #{storeId}
