mapper.select:
select
from t_wh_sku_barcode s left join t_wh_sku sku on sku.id = s.sku_id WHERE s.id in
#{id}

select

from t_wh_sku_barcode
where
    sku_id = #{skuId}
    and ou_id = #{ouId}

select


        from t_wh_sku_barcode
    where
        id = #{id}
        and ou_id = #{ouId}

select count(*) from t_wh_sku_barcode 


		and id != #{id}



		and sku_id = #{skuId}



		and bar_code like #{barCode}

mapper.update:
UPDATE t_wh_sku_barcode



            when (id = #{item.id} 

            	and ou_id = #{item.ouId}

            ) then #{item.barCode}




            when (id = #{item.id} 

            	and ou_id = #{item.ouId}

            ) then #{item.skuId}




            when (id = #{item.id} 

            	and ou_id = #{item.ouId}

            ) then #{item.createTime}




            when (id = #{item.id} 

            	and ou_id = #{item.ouId}

            ) then #{item.createdId}




            when (id = #{item.id} 

            	and ou_id = #{item.ouId}

            ) then #{item.lastModifyTime}




            when (id = #{item.id} 

            	and ou_id = #{item.ouId}

            ) then #{item.modifiedId}




            when (id = #{item.id}

                and ou_id = #{item.ouId}

            ) then #{item.quantity}




            when (id = #{item.id}

                and ou_id = #{item.ouId}

            ) then #{item.lifecycle}



WHERE

    (id = #{item.id} 

    	and ou_id = #{item.ouId}

    )

UPDATE t_wh_sku_barcode SET lifecycle = #{lifecycle},modified_id = #{userid},last_modify_time = #{lastModifyTime}
