mapper.update:
update sys_dictionary set lifecycle=#{lifecycle,javaType=java.lang.Integer,jdbcType=INTEGER}, operator_id=#{operatorId,javaType=java.lang.Long,jdbcType=BIGINT}, last_modify_time=#{modifyDate,javaType=java.util.Date,jdbcType=TIMESTAMP}
id in
#{item}
and is_edit=1

mapper.select:
select 
	from sys_dictionary
	where 
	group_value = #{groupValue} 
	and dic_value = #{dicValue}
