mapper.update:

        UPDATE sys_thread_deploy SET
	        thread_code = #{threadCode,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        description = #{description,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        thread_qty = #{threadQty,javaType=java.lang.Integer,jdbcType=INTEGER} ,
	        run_qty = #{runQty,javaType=java.lang.Integer,jdbcType=INTEGER} ,
	        create_time = #{createTime,javaType=java.util.Date,jdbcType=TIMESTAMP} ,
	        create_id = #{createId,javaType=java.lang.Long,jdbcType=BIGINT} ,
	        last_modify_time = #{lastModifyTime,javaType=java.util.Date,jdbcType=TIMESTAMP} ,
	        operator_id = #{operatorId,javaType=java.lang.Long,jdbcType=BIGINT} 
        WHERE 
	        id = #{id} 
	        and ou_id = #{ouId}
	        and last_modify_time	<=  #{lastModifyTime}

