mapper.update:
UPDATE t_wh_area SET lifecycle = #{lifecycle}, operator_id = #{userId}, last_modify_time = #{modifyDate}
WHERE id in

	#{id}

and ou_id = #{ouId}

mapper.select:
select count(*) 
	from t_wh_area area 
left join sys_dictionary sys on area.area_type = sys.dic_value


		and area.area_code like #{areaCode}


		and area.lifecycle = #{lifecycle}


		and area.area_name like #{areaName}


		and area.area_type = #{areaType}


		and area.ou_id = #{ouId}
		and sys.group_value = 'WAREHOUSE_AREA'


		and area.area_name like #{name1}


		and area.area_name like #{code1}

select area.id as areaId,
area.area_name as areaName,
area.area_code as areaCode,
sys.dic_label as areaType, 
area.lifecycle as lifecycle
from t_wh_area area 
left join sys_dictionary sys on area.area_type = sys.dic_value


		and area.area_code like #{areaCode}


		and area.lifecycle = #{lifecycle}


		and area.area_name like #{areaName}


		and area.area_type = #{areaType}


		and area.ou_id = #{ouId}
		and sys.group_value = 'WAREHOUSE_AREA'


		and area.area_name like #{name1}


		and area.area_name like #{code1}

mapper.update:

        UPDATE t_wh_area SET
	        area_code = #{areaCode,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        area_name = #{areaName,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        area_type = #{areaType,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        create_time = #{createTime,javaType=java.util.Date,jdbcType=TIMESTAMP} ,
	        last_modify_time = now(),
	        operator_id = #{operatorId,javaType=java.lang.Long,jdbcType=BIGINT} ,
	        lifecycle = #{lifecycle,javaType=java.lang.Integer,jdbcType=INTEGER} 
        WHERE 
	        id = #{id} 
	        and ou_id = #{ouId}
	        and last_modify_time = #{lastModifyTime}

