mapper.select:
select 
    from t_wh_container_2nd_category as s
    left join sys_dictionary as syst on s.one_level_type = syst.dic_value AND syst.group_value = 'ONE_LEVEL_TYPE'
    where s.lifecycle = #{lifecycle}
     
		and s.one_level_type = #{oneLevelType}
	 
	 
		and s.ou_id = #{ouId}

mapper.update:
UPDATE t_wh_container_2nd_category SET lifecycle = #{lifecycle},operator_id = #{userid},last_modify_time = now()   
WHERE id in

	#{id}

and ou_id = #{ouId}

mapper.delete:
    
        delete from t_wh_container_2nd_category where
	        id = #{id} 
	        and ou_id = #{ouId}

    
        delete from t_wh_container_2nd_category 
        where id in
			
		#{item}			
			
  		and ou_id = #{ouId}

mapper.select:
	select 
    
	    from t_wh_container_2nd_category 
        where 
        id = #{id} 
        and ou_id = #{ouId}

select count(*) from t_wh_container_2nd_category

	
		and id != #{id}
	

	
		and category_code like #{categoryCode}
	
	
		and category_name like #{categoryName}
	
	
		and ou_id = #{ouId}
