mapper.select:
select count(*) from t_wh_location_templet



		and id != #{id}



		and templet_code like #{templetCode}


		and templet_name like #{templetName}


		and ou_id = #{ouId}

mapper.update:

        UPDATE t_wh_location_templet SET
	        templet_code = #{templetCode,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        templet_name = #{templetName,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        split_mark = #{splitMark,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        description = #{description,javaType=java.lang.String,jdbcType=VARCHAR} ,
	        create_time = #{createTime,javaType=java.util.Date,jdbcType=TIMESTAMP} ,
	        last_modify_time = now(),
	        operator_id = #{operatorId,javaType=java.lang.Long,jdbcType=BIGINT} ,
	        lifecycle = #{lifecycle,javaType=java.lang.Integer,jdbcType=INTEGER} 
        WHERE 
	        id = #{id} 
	        and ou_id = #{ouId}	
	        and last_modify_time	=  #{lastModifyTime}

