mapper.select:
select


    from t_wh_recommend_platform
    where
    platform_recommend_rule_id = #{platformRecommendRuleId}
    and ou_id = #{ouId}

select count(*) from t_wh_recommend_platform
where
platform_recommend_rule_id = #{platformRecommendRuleId}
and ou_id = #{ouId}

mapper.delete:
delete from t_wh_recommend_platform
where
platform_recommend_rule_id = #{platformRecommendRuleId}
and ou_id = #{ouId}
