mapper.select:
    	select 

	    from 
	    	t_wh_operation_line

				ou_id = #{ouId}

				and operation_id = #{operationId}

    	select 

	    from 
	    	t_wh_operation_line

				ou_id = #{ouId}
				and operation_id = #{operationId}

    	select 

	    from 
	    	t_wh_operation_line

				ou_id = #{ouId}
				and operation_id = #{operationId}
				and from_outer_container_id = #{fromOuterContainerId}

    	select 

	    from 
	    	t_wh_operation_line

				ou_id = #{ouId}

				and odo_id = #{odoId}


		group by odo_line_id

select sum(qty) from t_wh_operation_line 
	where ou_id=#{ouId}
	and from_inside_container_id=#{fromInsideContainerId} 
	and operation_id=#{operationId}
	and qty != complete_qty

select sum(qty) from t_wh_operation_line 
	where ou_id=#{ouId}
	and from_outer_container_id=#{fromOuterContainerId} 
	and operation_id=#{operationId}
	and qty != complete_qty

select count(*) from t_wh_operation_line line 
	where line.ou_id=#{ouId}
	and line.operation_id=#{operationId}
	and line.qty != line.complete_qty

select sum(line.qty) from t_wh_operation_line line 
	where line.ou_id=#{ouId}
	and line.operation_id=#{operationId}

select distinct operLine.id as invId, operLine.sku_id as skuId, operLine.from_location_id as locationId, operLine.from_outer_container_id as outerContainerId, operLine.from_inside_container_id as insideContainerId, operLine.qty as onHandQty, operLine.inv_status as invStatus, operLine.inv_type as invType, operLine.batch_number as batchNumber, operLine.mfg_date as mfgDate, operLine.exp_date as expDate, operLine.country_of_origin as countryOfOrigin, operLine.inv_attr1 as invAttr1, operLine.inv_attr2 as invAttr2, operLine.inv_attr3 as invAttr3, operLine.inv_attr4 as invAttr4, operLine.inv_attr5 as invAttr5, operLine.uuid as uuid, operLine.ou_id as ouId from t_wh_operation_line operLine where operLine.ou_id = #{ouId} and operLine.from_location_id = #{locationId} and operLine.operation_id = #{operationId}
and operLine.from_outer_container_id = #{outerContainerId}
and operLine.from_inside_container_id=#{insideContainerId}
and operLine.from_outer_container_id is null
and operLine.from_inside_container_id is null
and operLine.qty != operLine.complete_qty
