mapper.update:

UPDATE t_wh_outbound_facility_group SET
    facility_group_code = #{facilityGroupCode,javaType=java.lang.String,jdbcType=VARCHAR} ,
    facility_group_name = #{facilityGroupName,javaType=java.lang.String,jdbcType=VARCHAR} ,
    facility_group_type = #{facilityGroupType,javaType=java.lang.String,jdbcType=VARCHAR} ,
    working_storage_section_id = #{workingStorageSectionId,javaType=java.lang.Long,jdbcType=BIGINT} ,
    create_time = #{createTime,javaType=java.util.Date,jdbcType=TIMESTAMP} ,
    last_modify_time = now() ,
    operator_id = #{operatorId,javaType=java.lang.Long,jdbcType=BIGINT} ,
    lifecycle = #{lifecycle,javaType=java.lang.Integer,jdbcType=INTEGER}
WHERE 
    id = #{id} 
    and ou_id = #{ouId,javaType=java.lang.Long,jdbcType=BIGINT} 
    and last_modify_time    <=  #{lastModifyTime}

mapper.select:
select
from t_wh_outbound_facility_group where ou_id = #{ouId} and lifecycle = 1
and facility_group_type = #{type}
