mapper.select:
select c.id as id, c.asn_id as asnId, c.asn_line_id as asnLineId, c.sku_id as skuId, c.container_id as containerId, c.ext_container_code as extContainerCode, c.quantity as quantity, c.mfg_date as mfgDate, c.exp_date as expDate, date_format(c.mfg_date,'%Y-%m-%d %T') as mfgDateStr, date_format(c.exp_date,'%Y-%m-%d %T') as expDateStr, c.batch_no as batchNo, c.country_of_origin as countryOfOrigin, c.inv_status as invStatus, c.inv_attr1 as invAttr1, c.inv_attr2 as invAttr2, c.inv_attr3 as invAttr3, c.inv_attr4 as invAttr4, c.inv_attr5 as invAttr5, c.inv_type as invType, c.ou_id as ouId, c.is_caselevel as isCaselevel, c.create_time as createTime, c.created_id as createdId, c.last_modify_time as lastModifyTime, c.modified_id as modifiedId, sku.code as skuCode, sku.name as skuName, inv.name as invName, type.dic_label as invTypeLabel, inv1.dic_label as invAttr1Str, inv2.dic_label as invAttr2Str, inv3.dic_label as invAttr3Str, inv4.dic_label as invAttr4Str, inv5.dic_label as invAttr5Str, cont.code as containerCode, nd.category_name as categoryName, line.status as asnLineStatus from t_wh_carton c left join t_wh_sku sku on sku.id = c.sku_id left join t_wh_container cont left join t_wh_container_2nd_category nd on nd.id = cont.two_level_type on cont.id = c.container_id left join t_wh_inventory_status inv on inv.id = c.inv_status left join sys_dictionary type on type.dic_value = c.inv_type and type.group_value = 'INVENTORY_TYPE' left join sys_dictionary inv1 on inv1.dic_value = c.inv_attr1 and inv1.group_value = 'INVENTORY_ATTR_1' left join sys_dictionary inv2 on inv2.dic_value = c.inv_attr2 and inv2.group_value = 'INVENTORY_ATTR_2' left join sys_dictionary inv3 on inv3.dic_value = c.inv_attr3 and inv3.group_value = 'INVENTORY_ATTR_3' left join sys_dictionary inv4 on inv4.dic_value = c.inv_attr4 and inv4.group_value = 'INVENTORY_ATTR_4' left join sys_dictionary inv5 on inv5.dic_value = c.inv_attr5 and inv5.group_value = 'INVENTORY_ATTR_5' left join t_wh_asn_line line on line.id = c.asn_line_id
c.ou_id = #{ouid}
and c.asn_id = #{asnid}
and c.asn_line_id = #{asnlineid}
and c.sku_id = #{skuid}

    	select 
	    from t_wh_carton 
	    where id = #{id} and ou_id = #{ouid}
