mapper.select:
       select 
       from t_wh_sku_inventory_tobefilled  where
	       ou_id = #{ouId}

				 and location_id=#{locationId}

     select 
       from t_wh_sku_inventory_tobefilled  where
	       ou_id = #{ouId}

				 and outer_container_id=#{outerContainerId}


				 and inside_container_id=#{insideContainerId}

mapper.delete:
delete from
t_wh_sku_inventory_tobefilled
where ou_id=#{ouId} and id=#{id}

mapper.select:
select
	sum(invTobefilled.qty)
from
	t_wh_sku_inventory_tobefilled invTobefilled


		and invTobefilled.location_id = #{locationId} 


		and invTobefilled.outer_container_id = #{outerContainerId} 


		and invTobefilled.inside_container_id = #{insideContainerId}

SELECT skuInvToBeFilled.id AS id, skuInvToBeFilled.sku_id AS skuId, skuInvToBeFilled.location_id AS locationId, skuInvToBeFilled.outer_container_id AS outerContainerId, skuInvToBeFilled.inside_container_id AS insideContainerId, skuInvToBeFilled.customer_id AS customerId, skuInvToBeFilled.store_id AS storeId, skuInvToBeFilled.occupation_code AS occupationCode, skuInvToBeFilled.occupation_line_id AS occupationLineId, skuInvToBeFilled.qty AS onHandQty, skuInvToBeFilled.inv_status AS invStatus, skuInvToBeFilled.inv_type AS invType, skuInvToBeFilled.batch_number AS batchNumber, skuInvToBeFilled.mfg_date AS mfgDate, skuInvToBeFilled.exp_date AS expDate, skuInvToBeFilled.country_of_origin AS countryOfOrigin, skuInvToBeFilled.inv_attr1 AS invAttr1, skuInvToBeFilled.inv_attr2 AS invAttr2, skuInvToBeFilled.inv_attr3 AS invAttr3, skuInvToBeFilled.inv_attr4 AS invAttr4, skuInvToBeFilled.inv_attr5 AS invAttr5, skuInvToBeFilled.uuid AS uuid, skuInvToBeFilled.ou_id AS ouId, skuInvToBeFilled.occupation_code_source AS occupationCodeSource, location.pick_sort as pickSort FROM t_wh_sku_inventory_tobefilled AS skuInvToBeFilled LEFT JOIN t_wh_odo AS odo ON skuInvToBeFilled.occupation_code = odo_code LEFT JOIN t_wh_odo_line AS odoLine ON skuInvToBeFilled.occupation_line_id = odoLine.id LEFT JOIN t_wh_location AS location ON location.allocate_area_id = skuInvToBeFilled.location_id LEFT JOIN t_wh_sku AS sku ON sku.id = skuInvToBeFilled.sku_id LEFT JOIN t_wh_sku_mgmt AS skuMgmt ON skuMgmt.sku_id = sku.id WHERE skuInvToBeFilled.occupation_line_id in
#{item}
AND skuInvToBeFilled.ou_id = #{ouId}

select distinct inv.id as id, inv.sku_id as skuId, inv.location_id as locationId, inv.outer_container_id as outerContainerId, inv.inside_container_id as insideContainerId, inv.customer_id as customerId, inv.store_id as storeId, inv.occupation_code as occupationCode, inv.occupation_line_id as occupationLineId, inv.replenishment_code as replenishmentCode, inv.qty as qty, inv.inv_status as invStatus, inv.inv_type as invType, inv.batch_number as batchNumber, inv.mfg_date as mfgDate, inv.exp_date as expDate, inv.country_of_origin as countryOfOrigin, inv.inv_attr1 as invAttr1, inv.inv_attr2 as invAttr2, inv.inv_attr3 as invAttr3, inv.inv_attr4 as invAttr4, inv.inv_attr5 as invAttr5, inv.uuid as uuid, inv.ou_id as ouId, inv.last_modify_time as lastModifyTime from t_wh_operation_line operLine, t_wh_sku_inventory_tobefilled inv where operLine.replenishment_code = inv.replenishment_code and inv.replenishment_code IS NOT NULL and inv.ou_id = #{ouId} and operLine.operation_id = #{operationId} and inv.location_id = #{locationId}
and inv.outer_container_id = #{outerContainerId}
and inv.inside_container_id=#{insideContainerId}
and inv.outer_container_id is null
and inv.inside_container_id is null

mapper.delete:
delete from
	t_wh_sku_inventory_tobefilled
where 
	replenishment_code = #{replenishmentCode}
	and ou_id = #{ouId}
